import React, { useState, useEffect } from "react";
import "./App.css";
import desktopImage from "../../../assets/img/wider.jpg";
import mobileImage from "../../../assets/img/mobile.jpeg";
import { styled } from "@material-ui/core/styles";
import Badge from "@material-ui/core/Badge";
import Avatar from "@material-ui/core/Avatar";
import negar from "../../../assets/img/negar.png";
import Grid from '@material-ui/core/Grid';

const Item = styled(Paper)(({ theme }) => ({
  ...theme.typography.body2,
  padding: theme.spacing(1),
  textAlign: 'center',
  color: theme.palette.text.secondary,
}));
export default function SectionAbout() {
  const [windowWidth, setWindowWidth] = useState(window.innerWidth);
  const imageUrl = windowWidth >= 650 ? desktopImage : mobileImage;

  useEffect(() => {
    const handleWindowResize = () => {
      setWindowWidth(window.innerWidth);
    };

    window.addEventListener("resize", handleWindowResize);

    return () => {
      window.removeEventListener("resize", handleWindowResize);
    };
  }, []);

  const SmallAvatar = styled(Avatar)(({ theme }) => ({
    width: 120,
    height: 120,
    border: `2px solid ${theme.palette.background.paper}`,
  }));

  return (
    <div className="App" style={{ backgroundImage: `url(${imageUrl})` }}>
      <div className="App-content">
        <Grid container spacing={2}>
          <Grid item xs={4}>
            <Item>
              <Badge
                overlap="circular"
                anchorOrigin={{ vertical: "bottom", horizontal: "right" }}
                badgeContent={<SmallAvatar alt="Remy Sharp" src={negar} />}
              >
                <Avatar
                  alt="Travis Howard"
                  src={negar}
                  style={{ width: 220, height: 220 }}
                />
              </Badge>
            </Item>
          </Grid>
          <Grid item xs={8}>
            <Item>xs=8</Item>
          </Grid>
        </Grid>

        {/* <h1 className="pp">Welcome to my website!</h1>
        <p className="pp" style={{ fontSize: "23px" }}>
          Lorem ipsum dolor sit amet consectetur adipisicing elit.
          <br />
          Rem cumque esse odit asperiores?
          <br />
          Vitae quo, officiis voluptatibus id consectetur modi quaerat nulla!
          <br />
          Officiis dolor illo mollitia nam officia laborum eum.
          <br />
          Vitae quo, officiis voluptatibus id consectetur modi quaerat nulla!
          <br />
          Officiis dolor illo mollitia nam officia laborum eum.
        </p> */}
      </div>
    </div>
  );
}
