import React from "react";
import PropTypes from "prop-types";
import SwipeableViews from "react-swipeable-views";
import { makeStyles, useTheme } from "@material-ui/core/styles";
// import AppBar from "@material-ui/core/AppBar";
import Tabs from "@material-ui/core/Tabs";
import Tab from "@material-ui/core/Tab";
import Typography from "@material-ui/core/Typography";
// import Box from "@material-ui/core/Box";
// import GridContainer from "components/Grid/GridContainer.js";
import GridItem from "components/Grid/GridItem.js";
import SectionAvatars from "./SectionAvatar";
import Paper from "@material-ui/core/Paper";
import Avatar from "@material-ui/core/Avatar";
import nina from "../../../assets/img/portfolio.jpg";
import Box from "@material-ui/core/Box";
import box3 from "../../../assets/img/box3.jpg";
function TabPanel(props) {
  const { children, value, index, ...other } = props;

  return (
    <div
      role="tabpanel"
      hidden={value !== index}
      id={`full-width-tabpanel-${index}`}
      aria-labelledby={`full-width-tab-${index}`}
      {...other}
    >
      {value === index && (
        <Box p={3}>
          <Typography>{children}</Typography>
        </Box>
      )}
    </div>
  );
}

TabPanel.propTypes = {
  children: PropTypes.node,
  index: PropTypes.any.isRequired,
  value: PropTypes.any.isRequired,
};

function a11yProps(index) {
  return {
    id: `full-width-tab-${index}`,
    "aria-controls": `full-width-tabpanel-${index}`,
  };
}

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
    maxWidth: 500,
    // maxWidth: 1000,
    // display: "flex",
    backgroundColor: theme.palette.background.paper,
    // backgroundColor: "#ccc",
    // width: 500,
    // alignItems: "center",
    marginTop: 20,
    // alignContent: "center",
    // justifyContent: "center",
    // marginLeft: 330,
  },
  //   pro: {
  //     // display: "flex",
  //     flexGrow: 1,
  //     justifyContent: "center",
  //   },
  panel: {
    justifyContent: "center",
    alignItems: "center",
    width: 500,
  },
  secondAvatar: {
    width: 240,
    height: 240,
    justifyContent: "center",
    margin: theme.spacing(7),
  },
  box3: {

  }
}));

export default function SectionAbout() {
  const classes = useStyles();
  const theme = useTheme();
  const [value, setValue] = React.useState(0);

  const handleChange = (event, newValue) => {
    setValue(newValue);
  };

  const handleChangeIndex = (index) => {
    setValue(index);
  };

  return (
    <Box display="flex" p={1} bgcolor="background.paper">
      <Box p={1} flexGrow={1} bgcolor="grey.300">
        <div className={classes.pro}>
          <GridItem
            xs={12}
            sm={6}
            container
            // direction="row"
            justifyContent="center"
            // alignItems="center"
          >
            <div className={classes.root}>
              {/* <GridContainer justify="center"> */}
              <Paper square className={classes.root}>
                <Tabs
                  value={value}
                  onChange={handleChange}
                  indicatorColor="primary"
                  textColor="primary"
                  variant="fullWidth"
                  aria-label="full width tabs example"
                >
                  <Tab label="About Nina" {...a11yProps(0)} />
                  <Tab label="Experiences" {...a11yProps(1)} />
                </Tabs>
              </Paper>
            </div>
          </GridItem>

          {/* </GridContainer> */}
          <GridItem
            xs={12}
            sm={6}
            md={6}
            container
            direction="row"
            justifyContent="center"
            alignItems="center"
          >
            <SwipeableViews
              axis={theme.direction === "rtl" ? "x-reverse" : "x"}
              index={value}
              onChangeIndex={handleChangeIndex}
            >
              <TabPanel
                value={value}
                index={0}
                dir={theme.direction}
                className={classes.panel}
              >
                <SectionAvatars />
                Lorem ipsum dolor sit amet consectetur adipisicing elit.
                Aliquid, ipsum architecto ut debitis voluptatibus maiores rerum
                praesentium velit dignissimos est mollitia? Corporis, at aliquam
                earum neque dolor saepe qui placeat soluta perspiciatis,
                quibusdam quo cum harum animi culpa cumque veritatis numquam
                nisi accusantium deleniti consequuntur? Laborum corrupti neque
                veniam nemo earum facere. Nesciunt, rem eius illum corrupti illo
                sequi iste expedita, praesentium inventore incidunt hic eum
                reiciendis ab necessitatibus qui neque velit explicabo ut
                commodi ipsa! Libero ducimus ad vitae?
              </TabPanel>
              <TabPanel value={value} index={1} dir={theme.direction}>
                <Avatar
                  alt="ninaPic"
                  src={nina}
                  className={classes.secondAvatar}
                />
                Lorem ipsum dolor sit amet consectetur adipisicing elit. Illum,
                autem architecto. Molestiae quas ipsam numquam ratione hic
                corrupti modi ex fugiat expedita consequuntur aliquid itaque
                tempore, cumque totam laudantium error eveniet beatae. Eum,
                similique ullam! Mollitia possimus quod minus et saepe, cum eum
                repellat? Saepe illum asperiores officia repellat aliquid.
              </TabPanel>
              {/* <TabPanel value={value} index={2} dir={theme.direction}>
          Item Three
        </TabPanel> */}
            </SwipeableViews>
          </GridItem>
        </div>
      </Box>
      <Box p={1} bgcolor="grey.300">
        <img src={box3} alt="boxx" className={classes.box3}/>
      </Box>
    </Box>
  );
}
